function toggleChatbot() {
    document.getElementById('chatbotWindow').classList.toggle('active');
}

function handleChatKeypress(e) {
    if (e.key === 'Enter') {
        sendMessage();
    }
}

function sendMessage() {
    const input = document.getElementById('chatInput');
    const message = input.value.trim();

    if (!message) return;

    const messagesContainer = document.getElementById('chatbotMessages');

    messagesContainer.innerHTML += `
        <div class="user-message">
            <p>${message}</p>
        </div>
    `;

    chatHistory.push({ role: 'user', content: message });
    if (chatHistory.length > MAX_CHAT_HISTORY * 2) {
        chatHistory = chatHistory.slice(-MAX_CHAT_HISTORY * 2);
    }

    input.value = '';

    setTimeout(() => {
        const botResponse = generateBotResponse(message);
        messagesContainer.innerHTML += `
            <div class="bot-message">
                <p>${botResponse}</p>
            </div>
        `;
        chatHistory.push({ role: 'bot', content: botResponse });
        messagesContainer.scrollTop = messagesContainer.scrollHeight;
    }, 500);

    messagesContainer.scrollTop = messagesContainer.scrollHeight;
}

function generateBotResponse(userMessage) {
    const msg = userMessage.toLowerCase();

    const responses = {
        study: [
            "For effective studying, try the Pomodoro Technique: 25 minutes of focused study followed by a 5-minute break. It really helps maintain concentration!",
            "Active recall and spaced repetition are proven techniques. Instead of just reading, try to explain concepts in your own words.",
            "Create a study schedule and stick to it. Consistency is more important than marathon study sessions!"
        ],
        exam: [
            "Start preparing early and break down the syllabus into manageable chunks. Practice previous year questions regularly!",
            "During exams, read all questions first and tackle the ones you know well. This builds confidence and saves time.",
            "Get enough sleep before exams. A well-rested brain performs much better than a sleep-deprived one!"
        ],
        assignment: [
            "Break down assignments into smaller tasks and set deadlines for each. This makes large projects less overwhelming.",
            "Start early! Last-minute work often leads to stress and lower quality. Give yourself time to review and refine.",
            "Don't hesitate to ask professors or classmates for clarification. Understanding requirements clearly is half the battle!"
        ],
        time: [
            "Time management is crucial! Use tools like calendars and to-do lists. Prioritize tasks based on urgency and importance.",
            "Block out specific time slots for studying, breaks, and social activities. Balance is key to avoiding burnout!",
            "The Eisenhower Matrix can help: categorize tasks as urgent/important, important/not urgent, urgent/not important, or neither."
        ],
        stress: [
            "Remember to take breaks and practice self-care. Exercise, meditation, and hobbies can significantly reduce stress.",
            "Talk to someone if you're feeling overwhelmed - friends, family, or campus counseling services. You're not alone!",
            "Break large tasks into smaller ones. Accomplishing small goals builds momentum and reduces anxiety."
        ],
        library: [
            "The Main Library is open 24/7 during exam season! It's a great quiet space for focused studying.",
            "Reserve study rooms in advance through the library website. Group study rooms are perfect for collaborative projects!",
            "Check out the library's online resources - they have access to thousands of academic journals and e-books."
        ]
    };

    for (const [key, responseArray] of Object.entries(responses)) {
        if (msg.includes(key) || msg.includes(key + 's') || msg.includes(key + 'ing')) {
            return responseArray[Math.floor(Math.random() * responseArray.length)];
        }
    }

    if (msg.includes('hello') || msg.includes('hi') || msg.includes('hey')) {
        return "Hello! I'm here to help with your college and study-related questions. Ask me about studying, exams, assignments, time management, or campus resources!";
    }

    if (msg.includes('thank') || msg.includes('thanks')) {
        return "You're welcome! Happy to help. Feel free to ask if you have more questions! üòä";
    }

    if (msg.includes('help') || msg.includes('what can you do')) {
        return "I can help you with study tips, exam preparation, assignment strategies, time management, stress relief, and campus information. What would you like to know?";
    }

    return "That's a great question! I can help with study techniques, exam prep, assignments, time management, and dealing with academic stress. Could you be more specific about what you'd like to know?";
}

function updateFeedPreview() {
    const preview = document.getElementById('feedPreview');
    if (mockData.posts.length > 0) {
        const latest = mockData.posts[0];
        preview.textContent = `${latest.author}: ${latest.content.substring(0, 60)}...`;
    }
}

document.addEventListener('DOMContentLoaded', function() {
    const savedTheme = localStorage.getItem('darkMode');
    if (savedTheme === 'true') {
        document.body.classList.add('dark-mode');
        document.getElementById('themeToggle').checked = true;
    }

    updateFeedPreview();

    window.onclick = function(event) {
        const settingsModal = document.getElementById('settingsModal');
        const addEventModal = document.getElementById('addEventModal');

        if (event.target === settingsModal) {
            closeSettings();
        }
        if (event.target === addEventModal) {
            closeAddEventModal();
        }
    };
});
